import React, { useEffect, useState } from "react";
import { useApolloClient, gql } from "@apollo/client";
import RecipeStep from "./RecipeStep";

const GET_RECIPE_STEPS = gql`
  query AllRecipeSteps {
    recipeSteps {
      id
      step
      completed
    }
  }
`;

function RecipeStepList() {
  const client = useApolloClient();
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    client
      .query({
        query: GET_RECIPE_STEPS,
      })
      .then((response) => {
        setData(response.data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err);
        setLoading(false);
      });
  }, [client]);

  if (loading) return <p>Loading...</p>;
  if (error) {
    console.error(error);
    return <p>Error loading steps: {error.message}</p>;
  }
  if (!data || !data.recipeSteps) return <p>No steps found!</p>;

  return (
    <ol className="list-decimal list-inside p-4">
      {data.recipeSteps.map((step) => (
        <RecipeStep key={step.id} step={step} completed={step.completed} />
      ))}
    </ol>
  );
}

export default RecipeStepList;
