import React, { useEffect, useState } from "react";
import { useApolloClient, gql, useQuery } from "@apollo/client";
import RecipeStep from "./RecipeStep";
import Delete from "./Delete";
import Update from "./Update";
const GET_RECIPE_STEPS = gql`
  query AllRecipeSteps {
    recipeSteps {
      id
      step
      completed
    }
  }
`;

function RecipeStepList() {
  //copied this directly from Apollo docs
  const { loading, error, data } = useQuery(GET_RECIPE_STEPS, {
    pollInterval: 500,
  }); //muchhh easier way to query with apollo client wow
  if (loading) return "Loading...";
  if (error) return `Error! ${error.message}`;

  //this was another way to query i tried which just required a bit more code but did the saem thing as above
  //   const client = useApolloClient();
  //   const [data, setData] = useState(null);
  //   const [error, setError] = useState(null);

  //   useEffect(() => {
  //     client
  //       .query({
  //         query: GET_RECIPE_STEPS,
  //       })
  //       .then((response) => {
  //         setData(response.data);
  //       })
  //       .catch((err) => {
  //         setError(err);
  //       });
  //   }, [client]);

  //   if (error) {
  //     console.error(error);
  //     return <p>error{error.message}</p>;
  //   }
  //   if (!data || !data.recipeSteps) return <p>no steps right now</p>;

  return (
    <ol className="list-decimal list-inside p-4">
      {data.recipeSteps.map((step) => (
        <div key={step.id}>
          <RecipeStep
            id={step.id}
            step={step.step}
            completed={step.completed}
          />
          <div className="flex">
            <Update
              id={step.id}
              startStep={step.step}
              completed={step.completed}
            />
            <Delete id={step.id} />
          </div>
        </div>
      ))}
    </ol>
  );
}

export default RecipeStepList;
