import Button from "./Button";
import React, { useState } from "react";
import RecipeStep from "./RecipeStep";
import { useMutation, gql } from "@apollo/client";

const UPDATE_RECIPE_STEP = gql`
  mutation UpdateRecipeStep($id: ID!, $step: String!) {
    updateRecipeStep(data: { id: $id, step: $step }) {
      id
      step
      completed
    }
  }
`;

export default function Update({ id, startStep }) {
  const [step, setStep] = useState(startStep);
  const [input, setInput] = useState("");
  const [updateRecipeStep, { loading, error }] = useMutation(
    UPDATE_RECIPE_STEP,
    {
      variables: { id },
    }
  );

  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!input) return;

    const { data } = await updateRecipeStep({
      variables: { id, step },
      //copied this directly from apollo docs
      optimisticResponse: {
        updateStep: {
          id: id,
          step: step,
          __typename: "RecipeStep",
        },
      },
    });
    setInput("");
  };
  if (error) return <p>error: {error.message}</p>;
  if (loading) return "Loading...";
  return (
    <div>
      <input
        type="text"
        value={step}
        onChange={(e) => setStep(e.target.value)}
        placeholder="Edit Step"
        className="p-2"
      />{" "}
      <Button text={"Edit Step"} color={"lightgreen"} onClick={handleUpdate} />{" "}
    </div>
  );
}
