import React, { useState } from "react";
import RecipeStep from "./RecipeStep";
import "./style.css";
import { useMutation, gql } from "@apollo/client";

export default function RecipeStepForm() {
  const [input, setInput] = useState("");
  const [recipeSteps, setRecipeSteps] = useState({});

  const ADD_STEP = gql`
    mutation AddRecipeStep($step: String!) {
      addRecipeStep(step: $step) {
        id
        step
        completed
      }
    }
  `;

  return (
    <form
      className="flex flex-col space-y-3 mt-4 p-4 bg-white rounded shadow"
      onSubmit={(e) => {
        e.preventDefault();
        const id = `step-${Math.floor(Math.random() * Date.now())}`;
        setRecipeSteps({ ...recipeSteps, [id]: input });
        setInput("");
      }}
    >
      <label
        htmlFor="recipe-step"
        className="text-sm font-semibold text-darkgreen"
      >
        Add task:
      </label>
      <textarea
        id="recipe-step"
        name="step"
        rows="3"
        className="border border-gray p-2 rounded focus:ring-blue-500 focus:border-blue-500"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      ></textarea>
      <button
        id="add-btn"
        type="submit"
        className="py-2 px-4 bg-blue-500 text-white rounded bg-purple hover:bg-lightgreen"
      >
        Add
      </button>
    </form>
  );
}
