import React, { useState } from "react";
import RecipeStep from "./RecipeStep";
import "./style.css";
import { useMutation, gql } from "@apollo/client";

const ADD_STEP = gql`
  mutation AddRecipeStep($step: String!) {
    addRecipeStep(step: $step) {
      id
      step
      completed
    }
  }
`;

export default function RecipeStepForm() {
  const [input, setInput] = useState("");
  const [addRecipeStep] = useMutation(ADD_RECIPE_STEP);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input) return;
    const { data } = await addRecipeStep({ variables: { step: input } });
    onAddStep(data.addRecipeStep);
    setInput("");
  };
  return (
    <form
      className="flex flex-col space-y-3 mt-4 p-4 bg-white rounded shadow"
      onSubmit={handleSubmit}
    >
      <label
        htmlFor="recipe-step"
        className="text-sm font-semibold text-darkgreen"
      >
        Add task:
      </label>
      <textarea
        id="recipe-step"
        name="step"
        rows="3"
        className="border border-gray p-2 rounded focus:ring-blue-500 focus:border-blue-500"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      ></textarea>
      <button
        type="submit"
        className="py-2 px-4 bg-blue-500 text-white rounded hover:bg-lightgreen"
      >
        Add
      </button>
    </form>
  );
}

