import React from "react";
import Button from "./Button";
import { useMutation, gql } from "@apollo/client";

const DELETE_RECIPE_STEP = gql`
  mutation DeleteStep($id: ID!) {
    deleteRecipeStep(data: { id: $id }) {
      id
    }
  }
`;

export default function Delete({ id }) {
  const [deleteRecipeStep, { loading, error }] = useMutation(
    DELETE_RECIPE_STEP,
    {
      variables: { id },
      refetchQueries: [{ query: GET_RECIPE_STEPS }], // Automatically refetch the list of recipe steps after deleting
    }
  );

  const handleDelete = () => {
    deleteRecipeStep();
  };

  if (loading) return <p>Deleting...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return <Button text="Delete Step" color="red" onClick={handleDelete} />;
}
