import React, { useState } from "react";
import { useMutation, gql } from "@apollo/client";

const UPDATE_RECIPE_STEP = gql`
  mutation UpdateRecipeStep($id: ID!, $step: String!) {
    updateRecipeStep(data: { id: $id, step: $step }) {
      id
      step
      completed
    }
  }
`;

export default function RecipeStep({ id, step, completed }) {
  /**
   * TODO #5:
   * 1. Add state for tracking if step is completed
   * 2. Update styles based on state
   */
  const [isComplete, setComplete] = useState(completed);
  const [updateRecipeStep, { loading, error }] = useMutation(
    UPDATE_RECIPE_STEP,
    {
      variables: { id, completed },
    }
  );

  const handleCheckbox = async (e) => {
    console.log(e);
    e.preventDefault();
    setComplete(!isComplete);

    const { data } = await updateRecipeStep({
      variables: { id, step },
      //copied this directly from apollo docs to learn about optimistc response
      optimisticResponse: {
        updateStep: {
          id: id,
          completed: completed,
          __typename: "RecipeStep",
        },
      },
    });
    setStep("");
  };

  if (error) return <p>error: {error.message}</p>;
  if (loading) return "Loading...";
  return (
    <li>
      <label
        style={{ textDecoration: isComplete ? "line-through" : "none" }}
        className="m-2"
      >
        {step}
      </label>
      <input
        type="checkbox"
        checked={isComplete}
        onChange={handleCheckbox}
      ></input>
    </li>
  );
}
