import Button from "./Button";
import React, { useState } from "react";
import RecipeStep from "./RecipeStep";
import { useMutation, gql } from "@apollo/client";

const UPDATE_RECIPE_STEP = gql`
  mutation UpdateRecipeStep($id: ID!, $step: String!, $completed: Boolean!) {
    updateRecipeStep(data: { id: $id, step: $step, completed: $completed }) {
      id
      step
      completed
    }
  }
`;

export default function Update({ id, step }) {
  const [updateRecipeStep, { loading, error }] = useMutation(
    UPDATE_RECIPE_STEP,
    {
      variables: { id },
    }
  );

  const handleUpdate = () => {
    updateRecipeStep();
  };
  if (error) return <p>error: {error.message}</p>;
  if (loading) return "Loading...";
  return (
    <>
      {" "}
      <Button text={"Update Step"} color={"lightgreen"} />{" "}
    </>
  );
}
