import React from "react";
import Button from "./Button";
import { useMutation, gql } from "@apollo/client";

const DELETE_RECIPE_STEP = gql`
  mutation DeleteStep($id: ID!) {
    deleteRecipeStep(data: { id: $id }) {
      id
    }
  }
`;

export default function Delete({ id }) {
  const [deleteRecipeStep, { error }] = useMutation(DELETE_RECIPE_STEP, {
    variables: { id },
  });

  const handleDelete = () => {
    deleteRecipeStep();
  };
  if (error) return <p>error: {error.message}</p>;

  return (
    <div>
      {" "}
      <Button text={"Delete Step"} color={"red"} />
      <Button text={"Delete Step"} color={"red"} onClick={handleDelete} />
    </div>
  );
}
