import React, { useEffect, useState } from "react";
import { useApolloClient, gql } from "@apollo/client";
import RecipeStep from "./RecipeStep";

// const GET_RECIPE_STEPS = gql`
//   query AllRecipeSteps {
//     recipeSteps {
//       id
//       step
//       completed
//     }
//   }
// `;

const query = `
query {
    recipeSteps{
        id
        step
        completed
    }
}`;

const url = "http://127.0.0.1:8000/api/graphql";

const options = {
  method: "POST",
  headers: {
    "Content-Type": "application/json",
    Accept: "application/json",
  },
  body: JSON.stringify({
    query,
  }),
};

function RecipeStepList() {
  //   const client = useApolloClient();
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  //   useEffect(() => {
  //     setLoading(true);
  //     client
  //       .query({
  //         query: GET_RECIPE_STEPS,
  //       })
  //       .then((response) => {
  //         setData(response.data);
  //         setLoading(false);
  //       })
  //       .catch((err) => {
  //         setError(err);
  //         setLoading(false);
  //       });
  //   }, [client]);

  //   if (loading) return <p>Loading...</p>;
  //   if (error) {
  //     console.error(error);
  //     return <p>Error loading steps: {error.message}</p>;
  //   }
  //   if (!data || !data.recipeSteps) return <p>No steps found!</p>;
  useEffect(() => {
    fetch(url, options)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setData(data.data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) {
    console.error(error);
    return <p>Error loading steps: {error.message}</p>;
  }
  if (!data || !data.recipeSteps) return <p>No steps found!</p>;

  return (
    <ol className="list-decimal list-inside p-4">
      {data.recipeSteps.map((step) => (
        <RecipeStep key={step.id} step={step} completed={step.completed} />
      ))}
    </ol>
  );
}

export default RecipeStepList;
