import React, { useEffect, useState } from "react";
import RecipeStep from "./RecipeStep";
import "./style.css";
import { useQuery, gql } from "@apollo/client";
import RecipeStepForm from "./RecipeStepForm";
import RecipeStepList from "./RecipeStepList";

const GET_RECIPE_STEPS = gql`
  query GetRecipeSteps {
    recipeSteps {
      id
      step
      completed
    }
  }
`;

export default function App() {
  // Add states for tracking input and recipe steps
  const { loading, error, data } = useQuery(GET_RECIPE_STEPS);
  useEffect(() => {
    if (loading) console.log("Loading...");
    if (error) console.log("Error fetching data:", error);
    if (data) console.log("Data received:", data);
  }, [loading, error, data]);
  return (
    <div className="flex flex-col min-h-screen bg-blue p-4">
      <header className="bg-blue-500 text-darkgreen p-4 shadow-lg">
        <h1 className="text-lg md:text-2xl">Recipe Step Tracker</h1>
      </header>

      <main className="flex-grow">
        <RecipeStepForm />
        <RecipeStepList />
      </main>
    </div>
  );
}
