import React, { useState } from "react";
import RecipeStep from "./RecipeStep";
import "./style.css";
// import { useMutation, gql } from "@apollo/client";

// const ADD_RECIPE_STEP = gql`
//   mutation AddRecipeStep {
//     addRecipeStep(data: $data) {
//       id
