//       completed
//     }
//   }
// `;

const url = "http://localhost:8000/api/graphql";

// export default function RecipeStepForm() {
//   const [input, setInput] = useState("");
//   const [addRecipeStep, { loading, error }] = useMutation(ADD_RECIPE_STEP);

//   const handleSubmit = async (e) => {
//     e.preventDefault();
//     if (!input) return;
//     const { data } = await addRecipeStep({
//       variables: { data: { step: input, completed: false } },
//     });
//     console.log(data.addRecipeStep); // Log the data or handle it as needed
//     setInput("");
//   };

//   if (loading) return <p>Loading...</p>;
//   if (error) return <p>Error: {error.message}</p>;

export default function RecipeStepForm() {
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input) return;

    const mutation = `
        mutation AddRecipeStep($data: RecipeStepInput!) {
          addRecipeStep(data: $data) {
            id
            step
            completed
          }
        }
      `;

    const options = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        query: mutation,
        variables: { data: { step: input, completed: false } },
      }),
    };

    setLoading(true);
    setError(null);

    fetch(url, options)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Mutation successful:", data);
        setInput(""); // Clear input after successful mutation
      })
      .catch((err) => {
        console.error("Error performing mutation:", err);
        setError(err);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <form
      className="flex flex-col space-y-3 mt-4 p-4 bg-white rounded shadow"
      onSubmit={handleSubmit}
    >
      <label
        htmlFor="recipe-step"
        className="text-sm font-semibold text-darkgreen"
      >
        Add task:
      </label>
      <textarea
        id="recipe-step"
        name="step"
        rows="3"
        className="border border-gray p-2 rounded focus:ring-blue-500 focus:border-blue-500"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      ></textarea>
      <button
        type="submit"
        className="py-2 px-4 bg-purple text-white rounded hover:bg-lightgreen"
      >
        Add
      </button>
    </form>
  );
}

