import React, { useState } from "react";
import RecipeStep from "./RecipeStep";
import "./style.css";
import { useQuery, gql } from "@apollo/client";

const GET_LOCATIONS = gql`
  query GetLocations {
    locations {
      id
      name
      description
      photo
    }
  }
`;

export default function App() {
  // Add states for tracking input and recipe steps
  const [input, setInput] = useState("");
  const [recipeSteps, setRecipeSteps] = useState({});

  return (
    <div className="flex flex-col min-h-screen bg-blue p-4">
      <header className="bg-blue-500 text-darkgreen p-4 shadow-lg">
        <h1 className="text-lg md:text-2xl">Recipe Step Tracker</h1>
      </header>

      <main className="flex-grow">
        <form
          className="flex flex-col space-y-3 mt-4 p-4 bg-white rounded shadow"
          onSubmit={(e) => {
            e.preventDefault();
            const id = `step-${Math.floor(Math.random() * Date.now())}`;
            setRecipeSteps({ ...recipeSteps, [id]: input });
            setInput("");
          }}
        >
          <label
            htmlFor="recipe-step"
            className="text-sm font-semibold text-darkgreen"
          >
            Add task:
          </label>
          <textarea
            id="recipe-step"
            name="step"
            rows="3"
            className="border border-gray p-2 rounded focus:ring-blue-500 focus:border-blue-500"
            value={input}
            onChange={(e) => setInput(e.target.value)}
          ></textarea>
          <button
            id="add-btn"
            type="submit"
            className="py-2 px-4 bg-blue-500 text-white rounded bg-purple hover:bg-lightgreen"
          >
            Add
          </button>
        </form>

        <ol className="list-decimal list-inside p-4">
          {Object.entries(recipeSteps).map(([k, v]) => (
            <RecipeStep key={k} stepText={v} />
          ))}
        </ol>
      </main>
    </div>
  );
}
